ModuleMappings.DefaultScss:
$background-color: #e9ecef;
$link-color: #212529;
$mobile-breakpoint: 768px;

background-color: $background-color;
padding: 2rem 0;

.footer-links {
    display: flex;
    justify-content: start;
    padding-left: 0rem;

    .footer-link {
        padding: 0rem .5rem;
        text-decoration: underline;
        color: $link-color;

        &:first-child {
            padding-left: 0px;
        }

        &:hover {
            text-decoration: none;
        }
    }
}

.col:empty {
    display: none;
}

.caption-text {
    padding-top: 1rem;
    justify-content: start;
}

@media (max-width: $mobile-breakpoint) {
    text-align: center;

    .caption-text {
        justify-content: center;
    }

    .footer-links {
        flex-direction: column;
        align-items: center;

        .footer-link {
            padding: 0.5rem 0rem;
        }
    }
}

.social-icon {
    align-items: center;
    display: flex;
    justify-content: center;
    height: 45px;
    width: 45px;

    .icon-img {
        filter: invert(1); // make it black
    }
}

